settings.custom_liquid:
{%- capture contentForQuerystring -%}
  {{ content_for_header }}
{%- endcapture -%}

{%- assign pageUrl = contentForQuerystring
  | split: '"pageurl":"'
  | last
  | split: '"'
  | first
  | split: '.myshopify.com'
  | last
  | replace: '\/', '/'
  | replace: '%20', ' '
  | replace: '\u0026', '&'
-%}

{%- capture finalurl -%}
  https://{{ pageUrl }}
{%- endcapture -%}

{%- assign cc_param = finalurl 
  | split: 'cc='
  | last
  | split: '&'
  | first
-%}

{%- for drop in shop.metaobjects['creator'][cc_param].data.value['cc-creator-drops'] -%}
    {%- for product_drop in drop['cc-creator-drop-products'] -%} {%- assign product_id = product.id | append: "" -%}
   {%- if product_drop.id == product_id -%}

<div class='wrapper' style="display: flex; justify-content: space-between; align-items: center">
        <h1>{{ product_drop.enhancement.note }} - {{shop.metaobjects['creator'][cc_param].data.value['cc-creator-first-name']}} </h1>
</div>
      {%- endif -%}
    {%- endfor -%}
 {%- endfor -%}
